// app/javascript/bundles/tests/CreateTest.js
import React from 'react';
import {Redirect} from 'react-router-dom';

export default class CreateTest extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      pri:'',
      question:'',
      team_stages:'',
      appears_day:'' ,
      frequency:'' ,
      type:'' ,
      role: [] ,
      required: '',
      conditions: '',
      mapping: ''
    }
  }

  componentDidMount() {
    let initialRoles = [];
    fetch('api/v1/tests/1/get_roles')
        .then(response => {
            return response.json();
        }).then(data => {
        initialRoles = data.results.map((role) => {
            return role;
        });
        console.log(initialRoles);
        this.setState({
            roles: initialRoles,
        });
    });
}

  handleInputChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  }

  createTestRequest = (event) => {
    console.log('this.state', this.state);
    fetch('/api/v1/tests', {
      method: 'post',
      body: JSON.stringify(this.state),
      headers: { 'Content-Type': 'application/json' },
    }).then((response) => {
      // alert('Quiz created successfully');
      // location.href = '/';
      this.props.history.push("");
    });
  }

  render() {
    let roles = this.props.state.role;
        let optionItems = roles.map((role) =>
                <option key={role.name}>{role.name}</option>
            );
    const {pri, question, team_stages, appears_day, frequency, types, role, required, conditions, mapping} = this.state;
    return (
      <div>
        <h3>New Quiz</h3>
        <div>
          <label>Pri: </label>
          <input
            type='number'
            name='pri'
            value={pri}
            onChange={this.handleInputChange}
            />
        </div>
        <div>
          <label>Question: </label>
          <input
            type='text'
            name='question'
            value={question}
            onChange={this.handleInputChange}
            />
        </div>
        <div>
          <label>Team stages: </label>
          <input
            type='text'
            name='team_stages'
            value={team_stages}
            onChange={this.handleInputChange}
            />
        </div>
        <div>
          <label>Appears day: </label>
          <input
            type='text'
            name='appears_day'
            value={appears_day}
            onChange={this.handleInputChange}
            />
        </div>
        <div>
          <label>Frequency: </label>
          <input
            type='text'
            name='frequency'
            value={frequency}
            onChange={this.handleInputChange}
            />
        </div>
         <div>
          <label>Type: </label>
          <input
            type='text'
            name='types'
            value={types}
            onChange={this.handleInputChange}
            />
        </div>
         <div>
          <label>Role: </label>
          <select>
                {optionItems}
             </select>
        </div>
         <div>
          <label>required: </label>
          <input
            type='text'
            name='required'
            value={required}
            onChange={this.handleInputChange}
            />
        </div>
        <div>
          <label>conditions: </label>
          <input
            type='text'
            name='conditions'
            value={conditions}
            onChange={this.handleInputChange}
            />
        </div>
         <div>
          <label>Mapping: </label>
          <input
            type='text'
            name='mapping'
            value={mapping}
            onChange={this.handleInputChange}
            />
        </div>
        <button onClick={this.createTestRequest}>Create</button>
      </div>
    );
  }
}
